changeSet.sql:
SET @procedure_order_type_id = 0;
SET @procedure_concept_class_id = 0;

SELECT order_type_id into @procedure_order_type_id from order_type where name = 'Procedure Order';
SELECT concept_class_id into @procedure_concept_class_id from concept_class where name = 'Procedure';

insert into order_type_class_map (order_type_id, concept_class_id) values(@procedure_order_type_id, @procedure_concept_class_id);
